func main() {

	level := calc_level(chars)

	fmt.Printf("level: %v\n", level)
}

func calc_level(chars string) int {

	times_basement := 0
	level := 0

	for i, c := range chars {

		if c == '(' {
			level = level + 1
		} else {
			level = level - 1
		}

		if level == -1 && times_basement == 0 {
			fmt.Printf("Position to make him enter basement: %v", i+1)
			times_basement += 1
		}
	}

	return level
}
